import Card from "../Card/Card";
import { connect } from "react-redux";

export const Favorites = ({ myFavorites }) => {

    return (
        <>
        {
            myFavorites?.map( (fav) => {
                return (
                    <Card
                        key={fav.id}
                        id={fav.id}
                        name={fav.name}
                        species={fav.species}
                        gender={fav.gender}
                        image={fav.image}
                    />
                )
            })
        }
        </>
    )
};

const mapStateToProps = (state) => {
    return {
        myFavorites: state.myFavorites
    }
} 

export default connect(
    mapStateToProps,
    null
)(Favorites);
