import { Link } from "react-router-dom";
import styles from "./Card.module.css"
import { addFav, removeFav } from "../../redux/action";
import { connect, useDispatch } from "react-redux";
import React from "react";
import { useState, useEffect } from "react";

export function Card({name, species, gender, image, id, onClose, addFav, removeFav, myFavorites}) {

   const dispatch = useDispatch()

   const [isFav, setIsFav] = useState(false);

   const handleFavorite = () => {
      if( isFav === true ) {
         setIsFav(false);
         return () => dispatch(removeFav(id))
      }
      else {
         setIsFav(true);
         return () => dispatch(addFav({name, species, gender, image, id}))
      }
   };

   useEffect(() => {
      myFavorites.forEach((fav) => {
         if (fav.id === props.id) {
            setIsFav(true);
         }
      });
   }, [myFavorites]);

   return (
      <div className={styles.Container}>
            {
               isFav ? (
                  <button onClick={handleFavorite}>‚ù§Ô∏è</button>
               ) : (
                  <button onClick={handleFavorite}>ü§ç</button>
               )
            }
            <button onClick={() => onClose(id)} className={styles.Close}>X</button>
            <Link to={`/detail/${id}`} className={styles.CardLink}>
            <h3 className={styles.CardName}>{name}</h3>
            </Link>
            <img src={image} alt={name} />
            <h3>{species}</h3>
            <h3>{gender}</h3>
      </div>
   );
}

export function mapDispatchToProps(dispatch) {
   return {
      addFav: (character) => dispatch(addFav(character)),
      removeFav: (id) => dispatch(removeFav(id))
   }
};

export function mapStateToProps(state) {
   return {
      myFavorites: state.myFavorites
   }
}

export default connect(mapStateToProps, mapDispatchToProps)(Card);
