const initialState = {
    myFavorites: []
};

const rootReducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case "ADD_FAV":
            return {
                ...state,
                myFavorites: [...state.myFavorites, payload]
            };
        case "REMOVE_FAV":
            let filteredArr = state.myFavorites.filter( (personaje) => personaje.id !== parseInt(payload) )
            return {
                ...state,
                myFavorites: [...filteredArr]
            };
    
        default:
            return {
                ...state
            };
    }
}

export default rootReducer;
