import { useState } from "react";
import validation from "../../validation";

export default function LoginForm({login}) {

    const [form, setForm] = useState(
        {
        email: "",
        password: ""
        }
    );

    const [errors, setErrors] = useState(
        {}
    );

    const handleChange = (event) => {
        let property = event.target.name;
        let value = event.target.value;
        setForm({
            ...form,
            [property]: value
        })
        validation (property, value, errors, setErrors)
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        login(form)
        if (errors.length > 0) return alert('Debe llenar todos los campos')
        if (!errors.length) {
        setForm({
            email: "",
            password: ""
            }
        )
        setErrors(
            {}
        )
      return alert("Datos completos")}
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
            <div>
                <label>Email</label>
                <input type="email" name="email" value={form.email} onChange={handleChange}></input>
                <span>{errors?.email}</span>
            </div>
            <div>
                <label>Password</label>
                <input type="password" name="password" value={form.password} onChange={handleChange}></input>
                <span>{errors?.password}</span>
            </div>
            <div>
                <button type="submit">Log In</button>
            </div>
            </form>
        </div>
    )
}
