        axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
           if (data.name) {
              setCharacter(data);
           } else {
              window.alert('No hay personajes con ese ID');
           }
        });
        return setCharacter({});
     }, [id]);

     let {name, status, species, gender, origin, image} = character;
     if (name) return <p>NAME: {name}</p>
     if (status) return <p>STATUS: {status}</p>
     if (species) return <p>SPECIES: {species}</p>
     if (gender) return <p>GENDER: {gender}</p>
     if (origin) return <p>ORIGIN: {origin.name}</p>
     if (image) return <p>IMAGE: {image}</p>
}
